package shooter;

import javafx.scene.paint.Color;
import javafx.scene.shape.Rectangle;

/**
 *
 * @author Maciek
 */
public class Bullet extends Rectangle {

    double velocityY;
    double velocityX;
    Map map;
    Segment current;
    boolean active;

    Bullet(double x, double y, double vx, double vy, Map m) {
        super(x, y, 15, 5);
        setFill(Color.RED);
        velocityX = vx;
        velocityY = vy;
        map = m;
        active=true;
    }

    public boolean update() {
        if (!current.isColliding(this)) {
            System.out.print("Kula zmienia segment z segmentu "+map.segments.indexOf(current)+ " na " );
            if (velocityY != 0) {
                active=false;
                return true;
            }
            if (velocityX > 0) {
                for (int i = map.segments.indexOf(current); i < map.segments.size(); i++) {
                    if (map.segments.get(i).isColliding(this)) {
                        current = map.segments.get(i);
                    }
                }
            } else {
                for (int i = map.segments.indexOf(current); i >= 0; i--) {
                    if (map.segments.get(i).isColliding(this)) {
                        current = map.segments.get(i);
                    }
                }
            }
            System.out.println(map.segments.indexOf(current));
        }
        
        for (Ground g : current.platforms) {
            System.out.println(g);
            if (g.isColliding(this)) {
                System.out.println("IMPACT");
                active=false;
                return true;
            }
        }
        pushByX(velocityX);
        pushByY(velocityY);
        
        return false;
    }

    void pushByX(double x) {
        setTranslateX(getTranslateX() + x);
    }

    void pushByY(double y) {
        setTranslateY(getTranslateY() + y);
    }

    @Override
    public boolean equals(Object o) {
        return o instanceof Bullet && getTranslateX() == ((Bullet) o).getTranslateX() && getTranslateY() == ((Bullet) o).getTranslateY();
    }

}

