package shooter;

import javafx.scene.layout.Pane;
import javafx.scene.paint.Color;
import javafx.scene.shape.Line;

/**
 *
 * @author Maciek
 */
public class Enemy extends Player{
    
    Line leftEdge;
    Line rightEdge;
    
    void think(Player p){
        if(p.getTranslateX()<getTranslateX()-5){
            goLeft();
        }else if(p.getTranslateX()>getTranslateX()+5){
            goRight();
        }else{
            stop();
        }
    }
    
    
    public Enemy(Map map, Pane root,int segment){
        super(30,30,map,root);
        setFill(Color.RED);
        leftEdge=new Line(getTranslateX(),getTranslateY()+getHeight(),getTranslateX(),getTranslateY()+getHeight()+50);
        rightEdge=new Line(getTranslateX()+getWidth(),getTranslateY()+getHeight(),getTranslateX()+getWidth(),getTranslateY()+getHeight()+50);
        currentSegment=map.segments.get(segment);
    }
    
    @Override
    public void pushByX(double x){
        super.pushByX(x);
        leftEdge.setTranslateX(leftEdge.getTranslateX() + x);
        rightEdge.setTranslateX(rightEdge.getTranslateX() + x);
    }
    

    @Override
    public void pushByY(double y){
        super.pushByY(y);
        leftEdge.setTranslateY(leftEdge.getTranslateY() +y);
        rightEdge.setTranslateY(rightEdge.getTranslateY() + y);
    }
}

