package shooter;

import java.util.ArrayList;
import javafx.scene.layout.Pane;
import javafx.scene.paint.Color;
import javafx.scene.shape.Rectangle;

/**
 *
 * @author Maciek
 */
public class Ground extends Rectangle {

    ArrayList<Integer> segments;
    public final static double FALLV = 10;
    boolean isFalling;
    boolean isTriggered = false;
    int wait=0;
    double shift;

    Ground(double x, double y, double w, double h, double shift) {
        super(w, h);
        setTranslateX(x);
        setTranslateY(y);
        setFill(Color.BROWN);
        this.shift = shift;
        segments = new ArrayList();
    }

    Ground(double x, double y, double w, double h, double shift, boolean fall) {
        super(w, h);
        setTranslateX(x);
        setTranslateY(y);
        isFalling = fall;
        if (isFalling) {
            setFill(Color.BURLYWOOD);
        } else {
            setFill(Color.BROWN);
        }
        this.shift = shift;
        segments = new ArrayList();
    }

    public void addToPane(Pane pane) {
        if (!pane.getChildren().contains(this)) {
            pane.getChildren().add(this);
        }
    }

    @Override
    public String toString() {
        StringBuilder s = new StringBuilder((getTranslateX() + shift) + " " + getTranslateY() + " " + getWidth() + " " + getHeight()+ " " + isFalling);
        for (int i : segments) {
            s.append(" " + i);
        }
        return new String(s);
    }

    void pushByX(double x) {
        if (segments.size() != 0) {
            setTranslateX(getTranslateX() + x / segments.size());
        } else {
            setTranslateX(getTranslateX() + x);
        }

    }

    void fall() {
        setTranslateY(getTranslateY() + FALLV);
    }

    void correct(double x) {
        setTranslateX(getTranslateX() + x);
    }

    boolean isColliding(Rectangle block) {
        return intersects(block.getBoundsInParent());
    }

    void trigger() {
        isTriggered = true;
    }

    @Override
    public boolean equals(Object o) {
        return o instanceof Ground && this.isFalling == ((Ground) o).isFalling && this.getTranslateX() == ((Ground) o).getTranslateX();
    }

}

