package shooter;

import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.FileNotFoundException;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayList;
import javafx.scene.layout.Pane;

/**
 *
 * @author Maciek
 */
public class Map {

    ArrayList<Segment> segments;

    PrintWriter platformWriter;
    PrintWriter wallsWriter;
    Pane root;

    public Map() throws FileNotFoundException, IOException {
        segments = new ArrayList();
        for (int i = 0; i < 10; i++) {
            Segment s = new Segment(i * Shooter.SCREENW, 0, i);
            segments.add(s);

        }

        String s;
        BufferedReader reader = new BufferedReader(new FileReader("platforms.txt"));
        while ((s = reader.readLine()) != null) {
            String data[] = s.split(" ");
            if (data[0].isEmpty()) {
                continue;
            }
            double x = Double.parseDouble(data[0]);
            double y = Double.parseDouble(data[1]);
            double w = Double.parseDouble(data[2]);
            double h = Double.parseDouble(data[3]);
            boolean f = Boolean.parseBoolean(data[4]);
            Ground g = new Ground(x, y, w, h, 0, f);
            for (int i = 5; i < data.length; i++) {
                int seg = Integer.parseInt(data[i]);
                g.segments.add(seg);
                segments.get(seg).platforms.add(g);
            }
        }
        reader.close();

        reader.close();

    }

    public void addObjectsToPane(Pane pane) {
        root = pane;
        for (Segment s : segments) {
            s.addToPane(pane);
            for (Ground p : s.platforms) {
                p.addToPane(pane);
            }
        }

    }

    public void showSegments() {
        for (Segment s : segments) {
            s.show();
        }
    }

    public void removeSegments() {
        for (Segment s : segments) {
            s.remove();
        }
    }

    public void addPlatform(Ground g) {
        for (int i = 0; i < segments.size(); i++) {
            Segment s = segments.get(i);
            if (s.isColliding(g)) {
                s.platforms.add(g);
                g.segments.add(i);
            }
        }
        platformWriter.println(g);
    }

    public void endEdit() {
        removeSegments();

        platformWriter.close();
        wallsWriter.close();
    }

    public void startEdit() {
        showSegments();
        try {
            FileWriter fwp = new FileWriter("platforms.txt", true);
            BufferedWriter bwp = new BufferedWriter(fwp);
            platformWriter = new PrintWriter(bwp);
            FileWriter fww = new FileWriter("walls.txt", true);
            BufferedWriter bww = new BufferedWriter(fww);
            wallsWriter = new PrintWriter(bww);
        } catch (FileNotFoundException ex) {
            System.out.println("PROBLEM");
        } catch (IOException ex) {
            System.out.println("PROBLEM");
        }
    }

    void pushEverything(double x) {

        for (Segment s : segments) {
            s.pushByX(-x);
        }

    }

    void update(Player p) {
        for (Ground g : p.currentSegment.platforms) {
            if (g.isTriggered) {
                g.wait++;
                if (g.wait >= 15) {
                    g.fall();
                }
            }
        }
    }
}

