package shooter;

import java.util.ArrayList;
import javafx.scene.input.KeyCode;
import javafx.scene.layout.Pane;
import javafx.scene.paint.Color;
import javafx.scene.shape.Rectangle;

/**
 *
 * @author Maciek
 */
public class Player extends Rectangle {

    public static final double INFINITY = 10000;

    private Map map;
    Segment currentSegment;

    private final double MAXSPEEDX = 6;     //maksymalna prędkość gracza w osi x
    private final double MAXSPEEDY = 15;    //maksymalna prędkość gracza w osi y

    private final double HEIGHT;                //wysokość wyprostowanego gracza
    private final double CROUCHINGSPEED = 5;    //prędkość kucania
    private final double MAXCROUCH = 40;        //wysokość skulonego gracza

    private final double ACCELERATIONX = 0.4;   //przyspieszenie gracza w osi x
    private final double ACCELERATIONY = 0.8;   //wartość przyspieszenia ziemskiego podczas wznoszenia
    private final double GRAVITY = 0.5;         //wartość przyspieszenia ziemskiego podczas opadania

    private boolean isFalling;      //czy gracz jest w trakcie opadania
    private boolean isJumping;      //czy gracz jest w trakcie wznoszenia
    private boolean isGoingLeft;    //czy gracz idzie w lewo
    private boolean isGoingRight;   //czy gracz idzie w prawo
    private boolean isCrouching;

    private ArrayList<Bullet> bullets;

    private final double BULLETSPEED = 10;

    private double velocityX;       //aktualna prędkość gracza w osi x
    private double velocityY;       //aktualna prędkość gracza w osi y

    private Rectangle landingDetector;
    private Rectangle roofDetector;
    private Rectangle leftDetector;
    private Rectangle rightDetector;

    double landingPoint;
    double roofPoint;
    double leftWall;
    double rightWall;

    private final Pane root;
    private Ground fallingPlatform;

    public Player(double WIDTH, double HEIGHT, Map map, Pane root) {
        super(WIDTH, HEIGHT);
        this.HEIGHT = HEIGHT;
        setTranslateX(0);
        setTranslateY(0);
        this.map = map;
        this.currentSegment = map.segments.get(0);
        this.root = root;
        bullets = new ArrayList();
        setFill(Color.BLACK);

        landingDetector = new Rectangle(WIDTH, 50);
        landingDetector.setTranslateX(getTranslateX());
        landingDetector.setTranslateY(HEIGHT);
        landingDetector.setFill(Color.TRANSPARENT);
        landingDetector.setStroke(Color.ORANGE);

        roofDetector = new Rectangle(WIDTH, 50);
        roofDetector.setTranslateX(getTranslateX());
        roofDetector.setTranslateY(-roofDetector.getHeight());
        roofDetector.setFill(Color.TRANSPARENT);
        roofDetector.setStroke(Color.ORANGE);

        leftDetector = new Rectangle(50, HEIGHT - 1);
        leftDetector.setTranslateX(getTranslateX() - leftDetector.getWidth());
        leftDetector.setTranslateY(getTranslateY());
        leftDetector.setFill(Color.TRANSPARENT);
        leftDetector.setStroke(Color.ORANGE);

        rightDetector = new Rectangle(50, HEIGHT - 1);
        rightDetector.setTranslateX(getTranslateX() + WIDTH);
        rightDetector.setTranslateY(getTranslateY());
        rightDetector.setFill(Color.TRANSPARENT);
        rightDetector.setStroke(Color.ORANGE);

        velocityY = MAXSPEEDY;
    }

    void update() {
        checkCurrentSegment();

        move();
        crouch();
        jump();
        fall();

        ArrayList<Bullet> toRemove = new ArrayList();
        for (int i = 0; i < bullets.size(); i++) {
            if (bullets.get(i).active && bullets.get(i).update()) {
                bullets.get(i).setVisible(false);
                toRemove.add(bullets.get(i));
            }
        }
        bullets.remove(toRemove);

        if (isDead()) {
            reset();
        }
    }

    void goLeft() {
        isGoingRight = false;
        isGoingLeft = true;
    }

    void goRight() {
        isGoingLeft = false;
        isGoingRight = true;
    }

    void stop() {
        velocityX = 0;
        isGoingLeft = false;
        isGoingRight = false;
    }

    private void move() {
        if (isGoingLeft) {
            if (velocityX <= MAXSPEEDX) {
                velocityX += ACCELERATIONX;
            }
            detectLeftWall();
            if (getTranslateX() - velocityX > leftWall) {
                pushByX(-velocityX);
            } else {
                double d = getTranslateX() - leftWall;
                pushByX(-d);
            }

        } else if (isGoingRight) {
            if (velocityX <= MAXSPEEDX) {
                velocityX += ACCELERATIONX;
            }
            detectRightWall();

            if (getTranslateX() + getWidth() + velocityX < rightWall) {
                pushByX(velocityX);
            } else {
                double d = rightWall - getTranslateX() - getWidth();
                pushByX(d);
            }
        }

    }

    private void jump() {
        if (!isFalling && isJumping) {
            if (velocityY > 0) {   //dopóki gracz jeszcze nie straci prędkości
                isFalling = false;
                velocityY -= ACCELERATIONY;
                detectRoofPoint();
                if (getTranslateY() - velocityY > roofPoint) {
                    pushByY(-velocityY);
                } else {
                    double d = roofPoint - getTranslateY() + 1;
                    pushByY(d);
                    velocityY = 0;
                }

            } else {                //jeśli prędkość gracza w osi y nie jest dodatnia to znaczy, że zaczyna opadać
                isJumping = false;
                isFalling = true;
            }
        }
    }

    private void fall() {
        if (!isJumping && !isColliding()) {
            /*jeśli aktualna prędkość gracza w osi Y jest równa maksymalnej
              to oznacza, że jest na ziemi (domyślnie jego prędkość jest ustawiona na
              maksymalną, ale nie jest wykorzystywana dopóki nie skoczy) i teraz zaczyna spadać z 
              platformy. Trzeba wyzerować jego prędkość w osi Y aby nie zaczął opadać z prędkością
              maksymalną.
             */
            if (velocityY == MAXSPEEDY) {
                velocityY = 0;
            }
            isFalling = true;
            detectLandingPoint();
            // -GRAVITY jest niezbędne aby nie doszło do spełnienia warunku opisanego wcześniej
            // bez tego odejmowania gracz spadałby "skokowo"
            if (velocityY < MAXSPEEDY - GRAVITY) {
                velocityY += GRAVITY;
            }

            if (getTranslateY() + getHeight() + velocityY + 1 < landingPoint) {
                pushByY(velocityY);
            } else {
                double d = landingPoint - getTranslateY() - getHeight();
                pushByY(d);
                if(fallingPlatform!=null && isColliding(fallingPlatform)){
                    fallingPlatform.trigger();
                }
                isFalling = false;
            }
        }
    }

    private void crouch() {
        if (!isJumping && !isFalling) { //kucać wolno tylko wtedy gdy stoimy na ziemi
            if (isCrouching) {
                if (getHeight() > MAXCROUCH) {
                    if (getHeight() - CROUCHINGSPEED > MAXCROUCH) {
                        setHeight(getHeight() - CROUCHINGSPEED);
                        //zmieniamy również wysokość detektorów, aby nie występowały błędy kolizji
                        rightDetector.setHeight(getHeight() - 1);
                        leftDetector.setHeight(getHeight() - 1);
                    } else {
                        setHeight(MAXCROUCH);
                        rightDetector.setHeight(MAXCROUCH - 1);
                        leftDetector.setHeight(MAXCROUCH - 1);
                    }
                    //poprawiamy pozycję detektora podłogi, ponieważ bez tego pojawiłaby się szczelina między nim a graczem
                    landingDetector.setTranslateY(getTranslateY() + getHeight());
                }
            } else {
                //przed próbą wstania trzeba sprawdzić czy nie uderzymy w sufit
                detectRoofPoint();
                if (getHeight() < HEIGHT && roofPoint < landingPoint - HEIGHT) {
                    if (getHeight() + CROUCHINGSPEED < HEIGHT) {
                        setHeight(getHeight() + CROUCHINGSPEED);
                        rightDetector.setHeight(getHeight() - 1);
                        leftDetector.setHeight(getHeight() - 1);
                    } else {
                        setHeight(HEIGHT);
                        rightDetector.setHeight(HEIGHT - 1);
                        leftDetector.setHeight(HEIGHT - 1);
                    }
                    //musimy podnieść gracza do góry aby nie wyprostował się pod ziemią
                    pushByY(-CROUCHINGSPEED);
                    landingDetector.setTranslateY(getTranslateY() + getHeight());
                }
            }
        }
    }

    private void detectLeftWall() {
        //tworzymy listę obiektów, które kolidują z detektorem
        ArrayList<Ground> platforms = new ArrayList();
        for (Ground p : currentSegment.platforms) {
            if (isDetectorColliding(leftDetector, p)) {
                platforms.add(p);
            }
        }
        if (!platforms.isEmpty()) {
            //szukamy obiektu, który jest najbliżej gracza
            Ground max = platforms.get(0);
            for (int i = 1; i < platforms.size(); i++) {
                if (max.getTranslateX() + max.getWidth() < platforms.get(i).getTranslateX() + platforms.get(i).getWidth()) {
                    max = platforms.get(i);
                }
            }
            //na jego podstawie obliczamy współrzędną ściany, która może zatrzymać gracza
            leftWall = max.getTranslateX() + max.getWidth() + 1;
        } else {
            leftWall = -INFINITY;
        }
    }

    private void detectRightWall() {
        ArrayList<Ground> platforms = new ArrayList();
        for (Ground p : currentSegment.platforms) {
            if (isDetectorColliding(rightDetector, p)) {
                platforms.add(p);
            }
        }
        if (!platforms.isEmpty()) {
            Ground min = platforms.get(0);
            for (int i = 1; i < platforms.size(); i++) {
                if (min.getTranslateX() > platforms.get(i).getTranslateX()) {
                    min = platforms.get(i);
                }
            }
            rightWall = min.getTranslateX() - 1;
        } else {
            rightWall = INFINITY;
        }
    }

    void detectLandingPoint() {
        ArrayList<Ground> platforms = new ArrayList();
        for (Ground p : currentSegment.platforms) {
            if (isDetectorColliding(landingDetector, p)) {
                platforms.add(p);
            }
        }
        if (!platforms.isEmpty()) {
            Ground min = platforms.get(0);
            for (int i = 1; i < platforms.size(); i++) {
                if (min.getTranslateY() > platforms.get(i).getTranslateY()) {
                    min = platforms.get(i);
                }
            }
            if (min.isFalling) {
                fallingPlatform = min;
            }
            landingPoint = min.getTranslateY();
        } else {
            landingPoint = INFINITY;
        }
    }

    void detectRoofPoint() {
        ArrayList<Ground> platforms = new ArrayList();
        for (Ground p : currentSegment.platforms) {
            if (isDetectorColliding(roofDetector, p)) {
                platforms.add(p);
            }
        }
        if (!platforms.isEmpty()) {
            Ground max = platforms.get(0);
            for (int i = 1; i < platforms.size(); i++) {
                if (max.getTranslateY() + max.getHeight() < platforms.get(i).getTranslateY() + platforms.get(i).getHeight()) {
                    max = platforms.get(i);
                }
            }
            roofPoint = max.getTranslateY() + max.getHeight();
        } else {
            roofPoint = -INFINITY;
        }
    }

    boolean isDetectorColliding(Rectangle detector, Rectangle block) {
        return detector.getBoundsInParent().intersects(block.getBoundsInParent());
    }

    void startJumping() {
        if (!isFalling && !isJumping && !isCrouching) {
            isJumping = true;
            velocityY = MAXSPEEDY;
        }
    }

    boolean isJumping() {
        return isJumping;
    }

    boolean isFalling() {

        return isFalling;
    }

    public boolean isColliding(Rectangle block) {
        return getBoundsInParent().intersects(block.getBoundsInParent());
    }

    //zwraca true, jeśli gracz koliduje z dowolną platformą na mapie
    boolean isColliding() {
        for (Ground p : currentSegment.platforms) {
            if (isColliding(p)) {
                return true;
            }
        }
        return false;
    }

    //przesuwa gracza w osi X o wartość x
    public void pushByX(double x) {
        setTranslateX(getTranslateX() + x);
        landingDetector.setTranslateX(landingDetector.getTranslateX() + x);
        roofDetector.setTranslateX(roofDetector.getTranslateX() + x);
        leftDetector.setTranslateX(leftDetector.getTranslateX() + x);
        rightDetector.setTranslateX(rightDetector.getTranslateX() + x);
    }

    //przesuwa gracza w osi Y o wartość y
    public void pushByY(double y) {
        setTranslateY(getTranslateY() + y);
        landingDetector.setTranslateY(landingDetector.getTranslateY() + y);
        roofDetector.setTranslateY(roofDetector.getTranslateY() + y);
        leftDetector.setTranslateY(leftDetector.getTranslateY() + y);
        rightDetector.setTranslateY(rightDetector.getTranslateY() + y);
    }

    void addToPane() {
        root.getChildren().addAll(this);
    }

    boolean isMovingRight() {
        return isGoingRight;
    }

    double getVelocityX() {
        return velocityX;
    }

    boolean isMovingLeft() {
        return isGoingLeft;
    }

    boolean isDead() {
        return getTranslateY() > 1.3 * Shooter.SCREENH;
    }

    void reset() {
        setTranslateX(0);
        setTranslateY(0);
        landingDetector.setTranslateX(getTranslateX());
        landingDetector.setTranslateY(getHeight());

        roofDetector.setTranslateX(getTranslateX());
        roofDetector.setTranslateY(-roofDetector.getHeight());

        leftDetector.setTranslateX(-leftDetector.getWidth());
        leftDetector.setTranslateY(getTranslateX());

        rightDetector.setTranslateX(getWidth());
        rightDetector.setTranslateY(getTranslateX());

    }

    void startCrouching() {
        isCrouching = true;
    }

    void stopCrouching() {
        isCrouching = false;
    }

    void shoot(KeyCode keyCode) {
        /*
        switch (keyCode) {
            case UP:
                makeBullet(0, -BULLETSPEED);
                break;
            case DOWN:
                makeBullet(0, BULLETSPEED);
                break;
            case LEFT:
                makeBullet(-BULLETSPEED, 0);
                break;
            case RIGHT:
                makeBullet(BULLETSPEED, 0);
                break;
        }
         */
    }

    private void makeBullet(double vx, double vy) {
        Bullet b = new Bullet(getTranslateX() + getWidth() / 2, getTranslateY() + getHeight() / 2, vx, vy, map);
        bullets.add(b);
        root.getChildren().add(b);
    }

    void checkCurrentSegment() {
        if (!isColliding(currentSegment)) {
            for (int i = 0; i < map.segments.size(); i++) {
                if (isColliding(map.segments.get(i))) {
                    currentSegment = map.segments.get(i);
                }
            }
        }
    }

}

