package shooter;

import java.util.ArrayList;
import javafx.scene.layout.Pane;
import javafx.scene.paint.Color;
import javafx.scene.shape.Rectangle;

/**
 *
 * @author Maciek
 */
public class Segment extends Rectangle {

    ArrayList<Ground> platforms;
    int index;

    Segment(double x, double y,int i) {
        super(x, y, Shooter.SCREENW, Shooter.SCREENH);
        setFill(Color.TRANSPARENT);
        setStroke(Color.TRANSPARENT);
        setStrokeWidth(4);
        index=i;
        platforms = new ArrayList();
    }

    public boolean isColliding(Rectangle block) {
        return getBoundsInParent().intersects(block.getBoundsInParent());
    }

    public void addToPane(Pane pane) {
        pane.getChildren().add(this);
    }

    void pushByX(double x) {
        setTranslateX(getTranslateX() + x);
        for (Ground p : platforms) {
            p.pushByX(x);
        }
    }

    @Override
    public boolean equals(Object o) {
        if (o instanceof Segment) {
            return index == ((Segment) o).index;
        }
        return false;
    }

    void removeFromPane(Pane pane) {
        pane.getChildren().remove(this);
    }

    void show() {
        setStroke(Color.BLACK);
    }

    void remove() {
        setStroke(Color.TRANSPARENT);
    }

}

