package shooter;

import java.io.FileNotFoundException;
import java.io.IOException;
import java.util.ArrayList;
import javafx.animation.KeyFrame;
import javafx.animation.Timeline;
import javafx.application.Application;
import javafx.scene.Cursor;
import javafx.scene.Scene;
import javafx.scene.input.KeyCode;
import javafx.scene.layout.Pane;
import javafx.stage.Stage;
import javafx.util.Duration;
import javafx.scene.paint.Color;
import javafx.scene.text.Font;
import javafx.scene.text.Text;
import javafx.scene.transform.Rotate;

/**
 *
 * @author Maciek
 */
public class Shooter extends Application {

    public static double SCREENW = 1300;
    public static double SCREENH = 700;

    Timeline timer;
    Player player;
    Enemy enemy;
    Map map;

    boolean EDITMODE;

    boolean firstClick = false;

    double x;
    double y;
    double w;
    double h;

    double shift = 0;
    double shiftVelocity = 4;
    private Ground selected;

    @Override
    public void start(Stage primaryStage) {

        try {
            map = new Map();
        } catch (FileNotFoundException ex) {
            System.out.println("PROBLEM");
        } catch (IOException ex) {
            System.out.println("PROBLEM");
        }

        Text edit = new Text("Edit Mode");
        edit.setFill(Color.TRANSPARENT);
        edit.setFont(new Font(20));
        edit.setTranslateX(30);
        edit.setTranslateY(30);

        Pane root = new Pane();
        root.getChildren().add(edit);
        player = new Player(45, 80, map, root);
        enemy = new Enemy(map, root,0);
        player.addToPane();
        enemy.addToPane();
        map.addObjectsToPane(root);

        Scene scene = new Scene(root, SCREENW, SCREENH);
        scene.setCursor(Cursor.DEFAULT);

        scene.setOnKeyPressed(event -> {
            switch (event.getCode()) {
                case A:
                    if (EDITMODE) {
                        //wszystko x3, ponieważ bez tego przesówanie w trybie edycji wydaje się powolne
                        if(firstClick){
                            selected.pushByX(3*shiftVelocity);
                        }
                        map.pushEverything(-3 * shiftVelocity);
                        shift -= 3 * shiftVelocity;
                    } else {
                        player.goLeft();
                    }
                    break;
                case D:
                    if (EDITMODE) {
                        if(firstClick){
                            selected.pushByX(-3 * shiftVelocity);
                        }
                        map.pushEverything(3 * shiftVelocity);
                        shift += 3 * shiftVelocity;
                    } else {
                        player.goRight();
                    }
                    break;
                case W:
                    player.startJumping();
                    break;
                case S:
                    player.startCrouching();
                    break;
                case UP:
                    player.shoot(KeyCode.UP);
                    break;
                case DOWN:
                    player.shoot(KeyCode.DOWN);
                    break;
                case LEFT:
                    player.shoot(KeyCode.LEFT);
                    break;
                case RIGHT:
                    player.shoot(KeyCode.RIGHT);
                    break;
                case E:
                    if (EDITMODE) {                       
                        edit.setFill(Color.TRANSPARENT);
                        map.endEdit();
                        scene.setCursor(Cursor.DEFAULT);
                    } else {
                        edit.setFill(Color.RED);
                        map.startEdit();
                        scene.setCursor(Cursor.DEFAULT);
                    }
                    EDITMODE = !EDITMODE;
                    break;
                case X:
                    //wciśnięcie x wyzeruje platformę
                    //pozostanie ona na planszy, nie będzie miała wpływu na gracza i nie zostanie zapisana
                    if(EDITMODE && firstClick){
                        selected.setWidth(0);
                        selected.setHeight(0);
                        firstClick=false;
                    }
                    break;
                case L:
                    //drukuje lokalizacje gracza
                    System.out.println("x=" + player.getTranslateX() + " y=" + player.getTranslateY());
                    break;
            }
        });
        scene.setOnKeyReleased(event -> {
            switch (event.getCode()) {
                case A:
                    player.stop();
                    break;
                case D:
                    player.stop();
                    break;
                case S:
                    player.stopCrouching();
                    break;

            }
        });
        scene.setOnMouseClicked(event -> {
            if (EDITMODE) {
                if (!firstClick) {
                    firstClick=true;
                    x = event.getSceneX() + shift;
                    y = event.getSceneY();
                    Ground g = new Ground(x, y, 0, 0,shift);
                    //bez tego correcta, platforma mogłaby wylądować poza ekranem
                    g.correct(-shift);
                    root.getChildren().add(g);
                    selected = g;
                }else{
                    selected.shift=shift;
                    map.addPlatform(selected);
                    firstClick = false;
                }

            }
        });

        scene.setOnMouseMoved(event -> {
            if (EDITMODE && firstClick) {
                double H=event.getSceneY() - selected.getTranslateY();
                double W=event.getSceneX() - selected.getTranslateX();
                selected.setHeight(H);
                selected.setWidth(W);
            }
        });

        KeyFrame frame = new KeyFrame(Duration.millis(16), e -> {

            if (!EDITMODE) {
                player.update();
                enemy.think(player);
                enemy.update();
                map.update(player);
                if (player.getTranslateX() > SCREENW * 0.8 && player.isMovingRight()) {
                    map.pushEverything(shiftVelocity);
                    player.pushByX(-player.getVelocityX());
                    shift += shiftVelocity;
                } else if (player.getTranslateX() < SCREENW * 0.2 && player.isMovingLeft() && shift > 0) {
                    map.pushEverything(-shiftVelocity);
                    player.pushByX(player.getVelocityX());
                    shift -= shiftVelocity;
                }
            }

        });

        timer = new Timeline();
        timer.getKeyFrames().add(frame);
        timer.setCycleCount(Timeline.INDEFINITE);
        timer.play();

        primaryStage.setTitle("ShootMan");
        primaryStage.setScene(scene);
        primaryStage.setResizable(false);
        primaryStage.show();
    }

    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        launch(args);
    }

}

