        this.props = props;
    }

    render(rootId) {
        ReactDOM.render(<App {...this.props} />, document.getElementById(rootId));
    }
}

import React, { Component } from 'react';
import '../../css/3DView.css';

import vtkActor from 'vtk.js/Sources/Rendering/Core/Actor';
import vtkFullScreenRenderWindow from 'vtk.js/Sources/Rendering/Misc/FullScreenRenderWindow';
import vtkXMLImageDataReader from 'vtk.js/Sources/IO/XML/XMLImageDataReader';
import vtkVolume from 'vtk.js/Sources/Rendering/Core/Volume';
import vtkImageCropFilter from 'vtk.js/Sources/Filters/General/ImageCropFilter';

import addOrientationWidget from './utils/adders/addOrientationWidget';
import addVolumeToScene from './utils/adders/addVolumeToScene';

import configureImageActors from './utils/configurators/configureImageActors';
import configurePatientActor from './utils/configurators/configurePatientActor';
import configureDosesActor from './utils/configurators/configureDosesActor';
import configureCropVisualizationActor from './utils/configurators/configureCropVisualizationActor';

import refreshVolumeActors from './utils/refreshers/refreshVolumeActors';

import ControlPanel from './components/ControlPanel';
import { ERROR_TYPES } from './components/shared/Progress';

const DEFAULT_COLORS = ['#e6194b', '#3cb44b', '#ffe119', '#4363d8', '#f58231', '#911eb4', '#46f0f0', '#f032e6', '#bcf60c', '#fabebe', '#008080', '#e6beff', '#9a6324', '#fffac8', '#800000', '#aaffc3', '#808000', '#ffd8b1', '#000075', '#808080'];

const DEFAULT_VISIBILITY = {
    orientationWidget: true,
    patient: true,
    doses: false,
    volumeActors: true
};

export default class ThreeDView extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = {
            isLoaded: {
                doses: false,
                patient: false
            },
            patientDownloadProgress: null,
            dosesDownloadProgress: null,
            isVisible: DEFAULT_VISIBILITY,
            error: null
        }
        this.roiActors = {};
        this.threeDContainer = React.createRef();
    };

    handleVisibilityChange = (name) => (event) => {
        const { checked } = event.target;
        if (name === 'volumeActors') {
            refreshVolumeActors(checked, this);
        } else if (name === 'orientationWidget') {
            this.handleOrientationWidgetVisbilityChange(checked);
        }
        this.setState({ isVisible: { ...this.state.isVisible, [name]: checked } })
    }

    handleVolumeSelect = (name) => {
        this.setState({
            isVisible: {
                ...this.state.isVisible,
                patient: name === 'patient',
                doses: name === 'doses'
            }
        })
    }

    resetVisibility = () => {
        refreshVolumeActors(true, this);
        this.handleOrientationWidgetVisbilityChange(DEFAULT_VISIBILITY.orientationWidget);
        this.setState({ isVisible: DEFAULT_VISIBILITY });
    }

    patientProgressCallback = ({ loaded, total }) => {
        this.setState({ patientDownloadProgress: Math.floor((loaded / total) * 100) })
    }

    dosesProgressCallback = ({ loaded, total }) => {
        this.setState({ dosesDownloadProgress: Math.floor((loaded / total) * 100) })
    }

    componentDidMount() {
        const body = this.threeDContainer.current;
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
            background: [0, 0, 0],
            rootContainer: body,
            containerStyle: {
                margin: '0',
                padding: '0',
                position: 'relative',
                width: body.style.width,
                height: body.style.height,
                overflow: 'hidden'
            }
        });

        this.renderer = fullScreenRenderer.getRenderer();
        this.renderWindow = fullScreenRenderer.getRenderWindow();
        this.renderWindow.getInteractor().setDesiredUpdateRate(15.0);

        this.patientCropFilter = vtkImageCropFilter.newInstance();
        this.dosesCropFilter = vtkImageCropFilter.newInstance();
        this.patientActor = vtkVolume.newInstance();
        this.dosesActor = vtkVolume.newInstance();
        this.patientImageActors = {
            X: {},
            Y: {},
            Z: {}
        };
        this.dosesImageActors = {
            X: {},
            Y: {},
            Z: {}
        };
        this.patientCropVisualizationActors = {
            X: [vtkActor.newInstance(), vtkActor.newInstance()],
            Y: [vtkActor.newInstance(), vtkActor.newInstance()],
            Z: [vtkActor.newInstance(), vtkActor.newInstance()]
        };
        this.dosesCropVisualizationActors = {
            X: [vtkActor.newInstance(), vtkActor.newInstance()],
            Y: [vtkActor.newInstance(), vtkActor.newInstance()],
            Z: [vtkActor.newInstance(), vtkActor.newInstance()]
        };

        const patientReader = vtkXMLImageDataReader.newInstance();
        patientReader.setDataAccessHelper({ fetchBinary: () => this.props.fetchPatientVTI(this.patientProgressCallback) });
        patientReader.loadData().then(() => {
            this.patientDataRange = patientReader.getOutputData().getPointData().getScalars().getRange();
            this.patientOrigin = patientReader.getOutputData().getOrigin();
            this.patientExtent = patientReader.getOutputData().getExtent();
            this.patientCropFilter.setCroppingPlanes(...this.patientExtent);
            this.patientSpacing = patientReader.getOutputData().getSpacing();

            addVolumeToScene(this.patientActor, this);
            this.handleOrientationWidgetVisbilityChange = addOrientationWidget(this);
            configureImageActors(patientReader, this.patientImageActors, this);
            configureCropVisualizationActor(
                this.patientCropVisualizationActors,
                this.patientExtent,
                this.patientSpacing,
                this
            );

            this.props.fetchRoiNames()
                .then((response) => {
                    response.text().then((text) => {
                        const data = JSON.parse(text);
                        data.rois.forEach((name, i) => (
                            this.roiActors[name] = {
                                actor: null,
                                name,
                                checked: false,
                                color: DEFAULT_COLORS[i],
                                defaultColor: DEFAULT_COLORS[i]
                            }));
                    })
                    this.setState({
                        isLoaded: {
                            ...this.state.isLoaded,
                            patient: true
                        }
                    });
                })
                .catch((ex) => {
                    console.error('Fetching roi names failed', ex);
                    this.setState({ error: ex.xhr ? ERROR_TYPES.FETCH_ROI : ERROR_TYPES.LOAD_ROI });
                });
        }).catch((ex) => {
            console.error('Fetching patient vti failed', ex);
            this.setState({ error: ex.xhr ? ERROR_TYPES.FETCH_PATIENT : ERROR_TYPES.LOAD_PATIENT });
        });

        const dosesReader = vtkXMLImageDataReader.newInstance();
        dosesReader.setDataAccessHelper({ fetchBinary: () => this.props.fetchDosesVTI(this.dosesProgressCallback) });
        dosesReader.loadData().then(() => {
            this.dosesDataRange = dosesReader.getOutputData().getPointData().getScalars().getRange();
            this.dosesOrigin = dosesReader.getOutputData().getOrigin();
            this.dosesExtent = dosesReader.getOutputData().getExtent();
            this.dosesCropFilter.setCroppingPlanes(...this.dosesExtent);
            this.dosesSpacing = dosesReader.getOutputData().getSpacing();

            addVolumeToScene(this.dosesActor, this);
            configureImageActors(dosesReader, this.dosesImageActors, this);
            configureCropVisualizationActor(
                this.dosesCropVisualizationActors,
                this.dosesExtent,
                this.dosesSpacing,
                this
            );

            this.dosesActor.setVisibility(false);

            this.setState({
                isLoaded: {
                    ...this.state.isLoaded,
                    doses: true
                }
            });
        }).catch((ex) => {
            console.error('Fetching doses vti failed', ex)
            this.setState({ error: ex.xhr ? ERROR_TYPES.FETCH_DOSES : ERROR_TYPES.LOAD_DOSES });
        });

        configurePatientActor(patientReader, { ...this });
        configureDosesActor(dosesReader, { ...this });
    }

    render() {
        const height = this.props.height ? this.props.height : '100vh';

        return (
            <div style={{
                display: 'inline-flex',
                height,
                width: this.props.width ? this.props.width[0] + this.props.width[1] : '100vw'
            }}>
                <ControlPanel
                    {...this}
                    height={this.props.height}
                    isLoaded={this.state.isLoaded}
                    isVisible={this.state.isVisible}
                    patientDownloadProgress={this.state.patientDownloadProgress}
                    dosesDownloadProgress={this.state.dosesDownloadProgress}
                    error={this.state.error}
                    width={this.props.width && this.props.width[0]}
                />
                <div
                    ref={this.threeDContainer}
                    style={{
                        height,
                        width: this.props.width ? this.props.width[1] : 'calc(100vw - 350px)'
                    }} />
            </div>
        );
    }
}

import React, { useCallback, useState } from 'react';

import List from '@material-ui/core/List';

import removeCropVisualizationActors from '../utils/removers/removeCropVisualizationActors';

import refreshRoiColor from '../utils/refreshers/refreshRoiColor';
import refreshRoiVisibilityChange from '../utils/refreshers/refreshRoiVisibilityChange';
import refreshRoisCrop from '../utils/refreshers/refreshRoisCrop';
import refreshCropVisualization from '../utils/refreshers/refreshCropVisualization';
import refreshImages from '../utils/refreshers/refreshImages';
import refreshVolumesVisibility from '../utils/refreshers/refreshVolumesVisibility';

import resetCrop from '../utils/reseters/resetCrop';
import resetRois from '../utils/reseters/resetRois';
import resetImages from '../utils/reseters/resetImages';

import {
    VolumeWidget,
    ImageWidget,
    RoiWidget,
    TogglesWidget
} from './Widgets';
import Progress from './shared/Progress';

const OPTIONS = [{
    key: 'patient',
    text: 'Pacjent'
}, {
    key: 'doses',
    text: 'Dawki'
}];

export default function ControlPanel({
    patientDownloadProgress,
    dosesDownloadProgress,
    isVisible,
    isLoaded,
    error,
    height = '100vh',
    width = 350,
    ...props
}) {
    const [, updateState] = useState();
    const forceUpdate = useCallback(() => updateState({}), []);

    return (
        <div
            id='control-panel'
            style={{ minHeight: height, height, width }}
        >
            {(isLoaded.doses && isLoaded.patient) ? (
                <List component="nav" >
                    <VolumeWidget
                        options={OPTIONS}
                        renderWindow={props.renderWindow}
                        width={width - 20}
                        onOptionChange={(name) => {
                            props.handleVolumeSelect(name);
                            refreshVolumesVisibility(name, props);
                            props.renderWindow.render();
                        }}
                        patient={{
                            actor: props.patientActor,
                            extent: props.patientExtent,
                            cropFilter: props.patientCropFilter,
                            onCropChange: (axis, newValue) => {
                                refreshCropVisualization({
                                    axis,
                                    positions: newValue,
                                    cropVisualizationActors: props.patientCropVisualizationActors,
                                    origin: props.patientOrigin,
                                    spacing: props.patientSpacing
                                });
                                props.renderWindow.render();
                            },
                            onCropEnd: (axis) => {
                                removeCropVisualizationActors(axis, props.patientCropVisualizationActors);
                                refreshRoisCrop(props);
                                props.renderWindow.render();
                            }
                        }}
                        doses={{
                            actor: props.dosesActor,
                            extent: props.dosesExtent,
                            cropFilter: props.dosesCropFilter,
                            onCropChange: (axis, newValue) => {
                                refreshCropVisualization({
                                    axis,
                                    positions: newValue,
                                    cropVisualizationActors: props.dosesCropVisualizationActors,
                                    origin: props.dosesOrigin,
                                    spacing: props.dosesSpacing
                                });
                                props.renderWindow.render();
                            },
                            onCropEnd: (axis) => {
                                removeCropVisualizationActors(axis, props.dosesCropVisualizationActors);
                                props.renderWindow.render();
                            }
                        }}
                        onReset={() => {
                            resetCrop(props);
                            refreshRoisCrop(props);
                            props.renderWindow.render();
                            forceUpdate();
                        }}
                    />
                    <RoiWidget
                        roiActors={props.roiActors}
                        handleCheckbox={(roi) => refreshRoiVisibilityChange(roi, props)}
                        handleColorChange={(roi) => refreshRoiColor(roi, props)}
                        onReset={() => {
                            resetRois(props);
                            forceUpdate();
                        }}
                    />
                    <ImageWidget
                        options={OPTIONS}
                        patient={{
                            dataRange: props.patientDataRange,
                            extent: props.patientExtent,
                            imageActors: props.patientImageActors,
                            refresh: (newValue) => refreshImages(newValue, props.patientImageActors, props)
                        }}
                        doses={{
                            dataRange: props.dosesDataRange,
                            extent: props.dosesExtent,
                            imageActors: props.dosesImageActors,
                            refresh: (newValue) => refreshImages(newValue, props.dosesImageActors, props)
                        }}
                        onOptionChange={() => {
                            resetImages(props);
                            forceUpdate();
                        }}
                        onReset={() => {
                            resetImages(props);
                            forceUpdate();
                        }}
                        renderWindow={props.renderWindow}
                    />
                    <TogglesWidget
                        onChange={props.handleVisibilityChange}
                        onReset={props.resetVisibility}
                        visibility={isVisible}
                    />
                </List>
            ) : (
                    <Progress
                        patientProgress={patientDownloadProgress}
                        dosesProgress={dosesDownloadProgress}
                        error={error}
                    />
                )}
        </div>
    )
}

