import React, { useEffect, useRef } from 'react';

import triggerDragAndDrop from '../../../utils/others/triggerDragAndDrop';

export default function PiecewiseGaussianWidget({
    actor,
    preset,
    renderWindow,
    widget
}) {
    const widgetContainer = useRef(null);

    const handleReset = () => {
        widget.setGaussians([])
        widget.addGaussian(0.5, 1.0, 0.5, 0.5, 0.4);
        triggerDragAndDrop(widgetContainer.current.childNodes[0])
    }

    useEffect(() => {
        const sourceDS = actor.getMapper().getInputData();
        const dataArray =
            sourceDS.getPointData().getScalars() ||
            sourceDS.getPointData().getArrays()[0];
        const lookupTable = actor.getProperty().getRGBTransferFunction(0);
        const piecewiseFunction = actor.getProperty().getScalarOpacity(0);


        widget.updateStyle({
            backgroundColor: 'rgba(200, 200, 200, 1)',
            histogramColor: 'rgba(100, 100, 100, 0.5)',
            strokeColor: 'rgb(0, 0, 0)',
            activeColor: 'rgb(255, 255, 255)',
            handleColor: 'rgb(50, 150, 50)',
            buttonDisableFillColor: 'rgba(255, 255, 255, 0.5)',
            buttonDisableStrokeColor: 'rgba(0, 0, 0, 0.5)',
            buttonStrokeColor: 'rgba(0, 0, 0, 1)',
            buttonFillColor: 'rgba(255, 255, 255, 1)',
            strokeWidth: 2,
            activeStrokeWidth: 3,
            buttonStrokeWidth: 1.5,
            handleWidth: 3,
            iconSize: 18,
            padding: 10,
        });
        widget.addGaussian(0.5, 1.0, 0.5, 0.5, 0.4);
        widget.setDataArray(dataArray.getData());
        widget.setColorTransferFunction(lookupTable);
        widget.applyOpacity(piecewiseFunction);
        widget.setContainer(widgetContainer.current);
        widget.bindMouseListeners();

        widget.onOpacityChange(() => {
            const lookupTable = actor.getProperty().getRGBTransferFunction(0);

            widget.applyOpacity(actor.getProperty().getScalarOpacity(0));
            lookupTable.setMappingRange(...widget.getOpacityRange());

            if (!renderWindow.getInteractor().isAnimating()) {
                renderWindow.render();
            }
        });

        widget.onAnimation((start) => {
            if (start) {
                renderWindow.getInteractor().requestAnimation(widget);
            } else {
                renderWindow.getInteractor().cancelAnimation(widget);
                renderWindow.render();
            }
        });

        lookupTable.onModified(() => {
            widget.render();
            if (!renderWindow.getInteractor().isAnimating()) {
                renderWindow.render();
            }
        });

        window.addEventListener('resetPiecewiseGaussianWidget', handleReset);

        return () => {
            window.removeEventListener('resetPiecewiseGaussianWidget', handleReset);
        }
    }, []);
    useEffect(() => {
        triggerDragAndDrop(widgetContainer.current.childNodes[0]);
    }, [preset])

    return (
        <div ref={widgetContainer}></div>
    )
}

