import React, { useEffect } from 'react';

import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

export default function VolumeEdgeSlider({ actor, renderWindow, edge, onChange }) {
    const updateEdgeGradient = (_, value) => {
        if (value === 0) {
            actor.getProperty().setUseGradientOpacity(0, false);
        } else {
            const sourceDS = actor.getMapper().getInputData();
            const dataArray =
                sourceDS.getPointData().getScalars() ||
                sourceDS.getPointData().getArrays()[0];
            const dataRange = dataArray.getRange();
            actor.getProperty().setUseGradientOpacity(0, true);
            const minV = Math.max(0.0, value - 0.3) / 0.7;
            actor.getProperty().setGradientOpacityMinimumValue(0, (dataRange[1] - dataRange[0]) * 0.2 * minV * minV);
            actor.getProperty().setGradientOpacityMaximumValue(0, (dataRange[1] - dataRange[0]) * 1.0 * value * value);
        }

        renderWindow.render();
        onChange(value);
    };

    useEffect(() => {
        updateEdgeGradient(null, edge);
    }, [edge]);

    return (
        <>
            <Typography gutterBottom>
