import React, { useEffect } from 'react';

import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

export default function VolumeSpacingSlider({ actor, renderWindow, spacing, onChange }) {
    useEffect(() => {
        updateSpacing(null, spacing);
    }, [spacing]);

    const updateSpacing = (_, value) => {
        const sourceDS = actor.getMapper().getInputData();
        const sampleDistance = 0.7 * Math.sqrt(sourceDS
            .getSpacing()
            .map((v) => v * v)
            .reduce((a, b) => a + b, 0)
        );
        actor.getMapper().setSampleDistance(sampleDistance * 2 ** (value * 3.0 - 1.5));
        renderWindow.render();
        onChange(value);
    };

    return (
        <>
            <Typography gutterBottom>
                Gęstość
            </Typography>
            <Slider
                step={0.01}
                min={0}
                max={1}
                value={spacing}
                onChange={updateSpacing}
            />
        </>
    );
}

