import { useState, useEffect } from 'react';

export default function useCrop({
    axis,
    indexes,
    cropFilter,
    extent,
    onChange: propsOnChange,
    onEnd
}) {
    const initial = [
        cropFilter.getCroppingPlanes()[indexes[0]],
        cropFilter.getCroppingPlanes()[indexes[1]],
    ];
    const [range, setRange] = useState(initial);

    useEffect(() => {
        window.addEventListener('resetCropWidget', onReset);
        return () => window.removeEventListener('resetCropWidget', onReset);
    }, []);

    const onReset = () => {
        setRange([
            cropFilter.getCroppingPlanes()[indexes[0]],
            cropFilter.getCroppingPlanes()[indexes[1]],
        ]);
    }

    const onChange = (_, newValue) => {
        propsOnChange(axis, newValue);
        setRange(newValue);
    }

    const onChangeCommitted = (_, newValue) => {
        const planes = cropFilter.getCroppingPlanes().slice();
        planes[indexes[0]] = newValue[0];
        planes[indexes[1]] = newValue[1];
        cropFilter.setCroppingPlanes(...planes);
        onEnd(axis);
    }

    return {
        max: extent,
        value: range,
        onChange,
        onChangeCommitted
    }
}

