import { useState } from 'react';

import vtkColorMaps from 'vtk.js/Sources/Rendering/Core/ColorTransferFunction/ColorMaps';
import vtkPiecewiseGaussianWidget from 'vtk.js/Sources/Interaction/Widgets/PiecewiseGaussianWidget';

const DEFAULT_PRESET = vtkColorMaps.rgbPresetNames[10];
const DEFAULT_EDGE = 0.4;
const DEFAULT_SPACING = 0.4;
const DEFAULT_SHADOW = "1";

export default function useVolumeWidget({
    actor,
    renderWindow,
    width,
}) {
    const [edge,setEdge] = useState(DEFAULT_EDGE);
    const [preset, setPreset] = useState(DEFAULT_PRESET);
    const [spacing, setSpacing] = useState(DEFAULT_SPACING);
    const [shadow, setShadow] = useState(DEFAULT_SHADOW);

    const widget = vtkPiecewiseGaussianWidget.newInstance({
        numberOfBins: 256,
        size: [width, 150],
    });

    const updateColorMapPreset = (value) => {
        const dataRange = widget.getOpacityRange()
        const lookupTable = actor.getProperty().getRGBTransferFunction(0);

        lookupTable.applyColorMap(vtkColorMaps.getPresetByName(value));
        lookupTable.setMappingRange(...dataRange);
        lookupTable.updateRange();
        renderWindow.render();
    }

    const onReset = () => {
        updateColorMapPreset(DEFAULT_PRESET);
        setPreset(DEFAULT_PRESET);
        setEdge(DEFAULT_EDGE);
        setSpacing(DEFAULT_SPACING);
        setShadow(DEFAULT_SHADOW);
        window.dispatchEvent(new CustomEvent('resetPiecewiseGaussianWidget'));
    }

    return {
        onReset,
        actor,
        renderWindow,
        widget,
        edge,
        onEdgeChange: (newEdge) => setEdge(newEdge),
        preset,
        onPresetChange: (newPreset) => {
            updateColorMapPreset(newPreset);
            setPreset(newPreset);
        },
        spacing,
        onSpacingChange: (newSpacing) => setSpacing(newSpacing),
        shadow,
        onShadowChange: (newShadow) => setShadow(newShadow)
    }
}

