import vtkCubeSource from 'vtk.js/Sources/Filters/Sources/CubeSource';
import vtkMapper from 'vtk.js/Sources/Rendering/Core/Mapper';
import vtkAppendPolyData from 'vtk.js/Sources/Filters/General/AppendPolyData';

const createCube = ([y, x, z], spacing, [orX, orY, orZ], appendPolyData) => {
    let cube = vtkCubeSource.newInstance({ xLength: spacing[0], yLength: spacing[1], zLength: spacing[2] });
    cube.setCenter([
        orX + x * spacing[0] + spacing[0] / 2,
        orY + y * spacing[1] + spacing[1] / 2,
        orZ + z * spacing[2] + spacing[2] / 2
    ]);
    appendPolyData.addInputConnection(cube.getOutputPort());
}

export default (points, spacing, origin, cropFilter) => {
    const appendPolyData = vtkAppendPolyData.newInstance();
    const cube = vtkCubeSource.newInstance();
    cube.setXLength(0);
    cube.setYLength(0);
    cube.setZLength(0);
    appendPolyData.setInputConnection(cube.getOutputPort());

    const [
        minX,
        maxX,
        minY,
        maxY,
        minZ,
        maxZ
    ] = cropFilter.getCroppingPlanes();
    points
        .filter(([y, x, z]) => (
            (x >= minX && x <= maxX) &&
            (y >= minY && y <= maxY) &&
            (z >= minZ && z <= maxZ)
        ))
        .forEach((point) => createCube(point, spacing, origin, appendPolyData));

    const mapper = vtkMapper.newInstance();
    mapper.setInputConnection(appendPolyData.getOutputPort());
    return mapper;
}

export default (hex) => {
