        parseInt(result[2], 16) / 255,
        parseInt(result[3], 16) / 255
    ] : null;
};

export default function (elemDrag) {

    // function for triggering mouse events
    const fireMouseEvent = function (type, elem, x, y) {
        const evt = document.createEvent('MouseEvents');
        evt.initMouseEvent(type, true, true, window, 1, 1, 1, x, y, false, false, false, false, 0, elem);
        elem.dispatchEvent(evt);
    };

    // calculate positions
    const pos = elemDrag.getBoundingClientRect();
    const center1X = Math.floor((pos.left + pos.right) / 2);
    const center1Y = Math.floor((pos.top + pos.bottom) / 2);

    const center2X = center1X + 1;
    const center2Y = center1Y + 1;

    // mouse over dragged element and mousedown
    fireMouseEvent('mousemove', elemDrag, center1X, center1Y);
    fireMouseEvent('mouseenter', elemDrag, center1X, center1Y);
    fireMouseEvent('mouseover', elemDrag, center1X, center1Y);
    fireMouseEvent('mousedown', elemDrag, center1X, center1Y);

    // start dragging process over to drop target
    fireMouseEvent('dragstart', elemDrag, center1X, center1Y);
    fireMouseEvent('drag', elemDrag, center1X, center1Y);
    fireMouseEvent('mousemove', elemDrag, center1X, center1Y);
    fireMouseEvent('drag', elemDrag, center2X, center2Y);

    fireMouseEvent('dragend', elemDrag, center2X, center2Y);
    fireMouseEvent('mouseup', elemDrag, center2X, center2Y);
};

export default ({
    axis,
    positions,
    cropVisualizationActors,
    origin,
    spacing
}) => {
    cropVisualizationActors[axis].forEach((actor, i) => {
        switch (axis) {
            case "X":
                actor.setPosition([
                    origin[0] + positions[i] * spacing[0],
                    origin[1],
                    origin[2]
                ]);
                break;
            case "Y":
                actor.setPosition([
                    origin[0],
                    origin[1] + positions[i] * spacing[1],
                    origin[2]
                ]);
                break;
            case "Z":
                actor.setPosition([
                    origin[0],
                    origin[1],
                    origin[2] + positions[i] * spacing[2]
                ]);
                break;
        }
        actor.setVisibility(true);
    });
}

