import hexToRgb from '../others/hexToRgb';

export default ({ roiActors, renderWindow }) => {
    for (const [_, actor] of Object.entries(roiActors)) {
        if (actor.checked) {
            actor.actor.setVisibility(false);
            actor.checked = false;
        }
        if (actor.color !== actor.defaultColor) {
            actor.color = actor.defaultColor;
            actor.actor && actor.actor.getProperty().setColor(...hexToRgb(actor.defaultColor));
        }
    }
    renderWindow.render();
}

