const MongoClient = require('mongodb').MongoClient;
const { mongoDbDatabaseName, mongoDbProjectsCollectionName, mongoDbUri } = require('config');


const index = async () => {

	const client = new MongoClient(mongoDbUri);
	await client.connect();

	const session = client.startSession();

	try {
		await session.withTransaction(async () => {
			const duplicates = await client
				.db(mongoDbDatabaseName)
				.collection(mongoDbProjectsCollectionName)
				.aggregate([{
					'$addFields': {
						'ident': {
							'$concat': ['$owner', '-', '$repo'],
						},
					},
				}, {
					'$group': {
						'_id': '$ident',
						'count': { '$sum': 1 },
					},
				}, {
					'$match': {
						'count': { '$gt': 1 },
					},
				}])
				.toArray();

			for (let { _id } of duplicates) {
				const [owner, repo] = _id.split('-');

				await client
					.db(mongoDbDatabaseName)
					.collection(mongoDbProjectsCollectionName)
					.deleteMany({ owner, repo });

				console.log(`Duplicated redpos ${_id} were removed`);
			}
		});
	} finally {
		await session.endSession();
		await client.close();
	}
};

