const fetchGithub = require('../utils/fetchGithubRestApi');
const fetchUser = require('./fetchUser');

const PAGE_MAX_SIZE = 100;
/*
	Github REST API przechowuje szczegółowe informacje tylko 500
	najbardziej aktywnych współautorów. Ze wględu na wydajność.
	https://docs.github.com/en/rest/reference/repos#list-repository-contributors
*/
const GITHUB_CONTRIBUTORS_SPECIFIC_INFO_LIMIT = 500;


module.exports = async ({ owner, repo }) => {
	let results = [];

	for (let page = 0; page < GITHUB_CONTRIBUTORS_SPECIFIC_INFO_LIMIT / PAGE_MAX_SIZE; page++) {
		const contributors = await fetchGithub('GET /repos/{owner}/{repo}/contributors', {
			owner,
			page,
			perPage: PAGE_MAX_SIZE,
			repo,
		});

		for (let i in contributors) {
			contributors[i] = {
				...contributors[i],
				userDetails: await fetchUser({ username: contributors[i].login }),
			};
		}

		results = results.concat(contributors);
		if (contributors.length < PAGE_MAX_SIZE) {
			break;
		}
	}

	return results.map(({
		contributions,
		login,
		siteAdmin,
		type,
		userDetails,
	}) => ({
		contributions,
		login,
		siteAdmin,
		type,
		userDetails,
	}));
};

