const MongoClient = require('mongodb').MongoClient;
const {
	mongoDbDatabaseName,
	mongoDbProjectsCollectionName,
	mongoDbUri,
	starsMinimalNumber,
} = require('config');
const hasBigAmountOfImportantLanguages = require('./utils/hasBigAmountOfImportantLanguages');
const fetchBasicInfo = require('./fetchers/fetchBasicInfo');
const fetchContributors = require('./fetchers/fetchContributors');
const fetchClosedIssues = require('./fetchers/fetchClosedIssues');
const fetchLanguages = require('./fetchers/fetchLanguages');
const fetchUser = require('./fetchers/fetchUser');

const framework = process.argv[2];

const index = async () => {

	const client = new MongoClient(mongoDbUri);
	await client.connect();

	const session = client.startSession();

	try {
		await session.withTransaction(async () => {
			const collection = client.db(mongoDbDatabaseName).collection(mongoDbProjectsCollectionName);
			const repos = await collection
				.find({ framework, ownerDetails: { $exists: false } })
				.sort({ stars: -1 })
				.toArray();

			for (let { _id, owner, repo } of repos) {
				const { openIssuesCount, forks, stars, ...basicInfo } = await fetchBasicInfo({ owner, repo });
				const languages = await fetchLanguages({ owner, repo });
				if (!hasBigAmountOfImportantLanguages(languages) || (starsMinimalNumber > stars) || !basicInfo) {
					await collection.removeOne({ _id });
					console.log(`Removed ${repo} repository of ${owner}`);
					continue;
				}

				const closedIssuesCount = await fetchClosedIssues({ owner, repo });
				const contributors = await fetchContributors({ owner, repo });
				const ownerDetails = await fetchUser({ username: owner });

				await collection.updateOne({ _id }, {
					$set: {
						...basicInfo,
						contributors,
						forks,
						framework,
						issues: {
							closed: closedIssuesCount,
							open: openIssuesCount,
						},
						languages,
						ownerDetails,
						stars,
					},
				});
				console.log(`Fetched and updated ${repo} repository of ${owner}`);
			}
		});
	} finally {
		await session.endSession();
		await client.close();
	}
};

