const MongoClient = require('mongodb').MongoClient;
const scrapeDependents = require('./scrapers/scrapeDependents');
const {
	mongoDbDatabaseName,
	mongoDbProjectsCollectionName,
	mongoDbScrapingCollectionName,
	mongoDbUri,
	starsMinimalNumber,
} = require('config');

const owner = process.argv[2];
const framework = process.argv[3];

const readLastUrl = async (repo) => {
	let url;

	const client = new MongoClient(mongoDbUri);
	await client.connect();

	const session = client.startSession();

	try {
		await session.withTransaction(async () => {
			const collection = client.db(mongoDbDatabaseName).collection(mongoDbScrapingCollectionName);
			const result = await collection.findOne(
				{ repo, type: 'dependents' },
			);

			url = result && result.url;
		});
	} finally {
		await session.endSession();
		await client.close();
	}

	return url;
};

const saveLastUrl = async (repo, url) => {
	const client = new MongoClient(mongoDbUri);
	await client.connect();

	const session = client.startSession();

	try {
		await session.withTransaction(async () => {
			const collection = client.db(mongoDbDatabaseName).collection(mongoDbScrapingCollectionName);
			await collection.updateOne(
				{ repo, type: 'dependents' },
				{ $set: { url } },
				{ upsert: true },
			);
		});
	} finally {
		await session.endSession();
		await client.close();
	}
};

const index = async () => {
	const client = new MongoClient(mongoDbUri);
	await client.connect();

	let url = await readLastUrl(framework) || `https://github.com/${owner}/${framework}/network/dependents`;
	let shouldRun = true;

	while (shouldRun) {
		const { dependents, nextUrl } = await scrapeDependents(url);
		const result = dependents.filter(({ stars, forks }) => stars || forks);

		await saveLastUrl(framework, url);
		url = nextUrl;

		const session = client.startSession();

		try {
			await session.withTransaction(async () => {
				const collection = client.db(mongoDbDatabaseName).collection(mongoDbProjectsCollectionName);

				result
					.filter(({ stars }) => stars > starsMinimalNumber)
					.forEach(({ owner, repo, stars, forks }) => {
						collection.updateOne(
							{ owner, repo },
							{
								$set: {
									forks,
									framework,
									owner,
									repo,
									stars,
								},
							},
							{ upsert: true },
						).then(({ upsertedCount }) => upsertedCount && console.log(`Added new repo for ${framework}`));
					});
			});
		} catch (e) {
			console.log(e);
			shouldRun = false;
		} finally {
			await session.endSession();
		}
	}
};

