const { Octokit } = require('@octokit/core');
const pRetry = require('p-retry');
const camelcaseKeys = require('camelcase-keys');
const snakeCaseKeys = require('snakecase-keys');
const {
	githubRestApiToken,
	githubApiRequestLimitTimeout,
	githubApiRequestRetryAttempts,
} = require('config');

const octokit = new Octokit({ auth: githubRestApiToken });

const handleFetch = async (route, options) => {
	console.log({ options, route });
	const { data } = await octokit.request(route, snakeCaseKeys(options));

	return data;
};

module.exports = async (route, options) => {
	try {
		const data = await pRetry(
			() => handleFetch(route, options),
			{
				minTimeout: githubApiRequestLimitTimeout,
				onFailedAttempt: (error) => {
					if (error.status === 404) {
						throw new pRetry.AbortError('NOT FOUND');
					}
					console.log(error,'Github REST API requests limit reached, waiting...');
				},
				retries: githubApiRequestRetryAttempts,
			},
		);
	
		return camelcaseKeys(data);
	} catch (e) {
		console.log(e);
	}

	return {};
};

const { importantLanguages, importantLanguagesMinimalRatio } = require('config');

module.exports = (languages) => {
	const importantLangugesSum = importantLanguages.reduce((result, language) => result + (languages[language] || 0), 0);
	const langugagesSum = Object.values(languages).reduce((result, language) => result + language, 0);

	return (importantLangugesSum / langugagesSum) >= importantLanguagesMinimalRatio;
};

const pRetry = require('p-retry');
const axios = require('axios');
const {
	githubScrapeLimitTimeout,
	githubScrapeRetryAttempts,
} = require('config');

const handleScrape = async (route) => {
	console.log({ route });
	const { data } = await axios.get(route);

	return data;
};

module.exports = async (route) => {
	const html = await pRetry(
		() => handleScrape(route),
		{
			minTimeout: githubScrapeLimitTimeout,
			onFailedAttempt: () => console.log('Github scraping limit reached, waiting...'),
			retries: githubScrapeRetryAttempts,
		},
	);

	return html;
};

