const MongoClient = require('mongodb').MongoClient;
const fs = require('fs');
const { resolve } = require('path');
const { mongoDbDatabaseName, mongoDbUri } = require('config');
const downloadProject = require('./utils/downloadProject');
const createProject = require('./utils/createProject');
const analyzeProject = require('./utils/analyzeProject');
const getIssues = require('./utils/getIssues');
const getMeasures = require('./utils/getMeasures');
const deleteProject = require('./utils/deleteProject');

const framework = process.argv[2];

const index = async () => {
	let repos = [];
	do {
		const client = new MongoClient(mongoDbUri);
		await client.connect();

		const session = client.startSession();

		try {
			await session.withTransaction(async () => {
				const finalCollection = client.db(mongoDbDatabaseName).collection('final');
				const collection = client.db(mongoDbDatabaseName).collection(framework);
				repos = await collection
					.find({ ownerDetails: { $exists: true }, sonar: { $exists: false } })
					.sort({ stars: -1 })
					.limit(500)
					.toArray();

				for (let { _id, owner, repo, ...restProps } of repos) {
					const err = await downloadProject({ owner, repo });
					if (err) {
						fs.rmdirSync(resolve(`./sonarScanner/tmp/${repo}`), { recursive: true });
						continue;
					}
					await createProject({ name: repo });
					const anzErr = await analyzeProject({ repo });
					if (anzErr) {
						await deleteProject({ owner, repo });
						continue;
					}
					const issues = await getIssues({ name: repo });
					const measures = await getMeasures({ name: repo });
					if (!measures.size) {
						await deleteProject({ owner, repo });
						continue;
					}
					await finalCollection.insertOne({
						framework,
						owner,
						repo,
						sonar: {
							issues,
							measures,
						},
						...restProps,
					});
					await collection.removeOne({ _id });
					await deleteProject({ owner, repo });
					console.log(`Scanned and updated ${repo} repository of ${owner}`);
				}
			});
		} finally {
			await session.endSession();
			await client.close();
		}
	} while (repos.length);
};

index();

