const camelcaseKeys = require('camelcase-keys');
const fetchSonar = require('./fetchSonar');

const TYPES = ['CODE_SMELL', 'BUG', 'VULNERABILITY'];
const SEVERITY = ['INFO', 'MINOR', 'MAJOR', 'CRITICAL', 'BLOCKER'];

module.exports = async ({ name }) => {
	const result = TYPES
		.reduce((result, type) => ({
			...result,
			[type]: SEVERITY
				.reduce((result, severity) => ({
					...result,
					[severity]: 0,
				}), {}),
		}), {});

	for (const type of TYPES) {
		for (const severity of SEVERITY) {
			const { total } = await fetchSonar('/api/issues/search', {
				componentKeys: name,
				severities: severity,
				types: type,
			});

			result[type][severity] = result[type][severity] + total;
		}
	}

	return camelcaseKeys(result, { deep: true });
};

const camelcaseKeys = require('camelcase-keys');
const fetchSonar = require('./fetchSonar');

const PAGE_MAX_SIZE = 200;

module.exports = async ({ name }) => {
	const { metrics } = await fetchSonar('/api/metrics/search', { p: 1, ps: PAGE_MAX_SIZE });
	const domains = [...new Set(metrics.map(({ domain }) => domain))];
	const result = {};

	for (let currentDomain of domains) {
		const types = [...new Set(metrics.filter(({ domain }) => domain === currentDomain).map(({ type }) => type))];

		for (let currentType of types) {
			const keys = [...new Set(metrics.filter(({ domain, type }) => domain === currentDomain && type === currentType).map(({ key }) => key))];

			for (let key of keys) {
				const { component: { measures } } = await fetchSonar('/api/measures/component', { component: name, metricKeys: key });

				if (measures.length) {
					const [{ value }] = measures;

					if (!(currentDomain in result)) {
						result[currentDomain] = {};
					}
					if (!(currentType in result[currentDomain])) {
						result[currentDomain][currentType] = {};
					}
					result[currentDomain][currentType][key] = value;
				}
			}
		}
	}
	
	return camelcaseKeys(result, { deep: true });
};

