            FVersion = v.version;
            FBuild = v.build;
            FMatch = v.match;
            FPre = v.pre;

            var parts = splitVersion(v.version);
            FMajor = parts.major;
            FMinor = parts.minor;
            FSecurity = parts.security;
            FPatch = parts.patch;
        }

        // Public
        return {
            VersionString: VersionString,
            old: FOld,
            major: FMajor,
            minor: FMinor,
            security: FSecurity,
            patch: FPatch,
            version: FVersion,
            build: FBuild,
            pre: FPre,
            match: FMatch,

            check: function(query) {
                return check(query, this);
            },

            equals: function(query) {
                return equals(query, this);
            }
        };

        // Private
        function splitVersion(version) {
            var lmajor = null;
            var lminor = null;
            var lsecurity = null;
            var lpatch = null;

            if (version.length >= 1) {
                lmajor = version[0];
            }

            if (version.length >= 2) {
                lminor = version[1];
            }

            if (version.length >= 3) {
                lsecurity = version[2];
            }

            if (version.length >= 4) {
                lpatch = version[3];
            }

            return {
                major: lmajor,
                minor: lminor,
                security: lsecurity,
                patch: lpatch
          };
        }

        function VersionStringTokenizer(versionString) {
            // Convert the version string to lower case and strip all whitespace
            // from the beginning and end of the string.

            var FVersionString = versionString.toLowerCase().trim();
            var FIndex;
            var FCurrentToken = null;
            var FStack = Array();

            function isDigit(c) {
                var result = false;

                switch(c) {
                    case '0':
                    case '1':
                    case '2':
                    case '3':
                    case '4':
                    case '5':
                    case '6':
                    case '7':
                    case '8':
                    case '9':
                        result = true;
                        break;
                }

                return result;
            }

            function isLetter(c) {
                //return c.match("^[a-zA-Z]");
                var result = false;
                var lowerBoundLower = "a".charCodeAt(0);
                var upperBoundLower = "z".charCodeAt(0);
                var bound = c.charCodeAt(0);

                if (lowerBoundLower <= bound && bound <= upperBoundLower) {
                    result = true;
                }

                return result;
            }

            function start() {
                FIndex = 0;
            }

            function currentToken() {
                return FCurrentToken;
            }

            function pushToken(Token) {
                if (FCurrentToken != null) {
                    FStack.unshift(FCurrentToken);
                }

                FCurrentToken = Token;
            }

            function nextToken() {
                var tokenID = Token.Uninitialized;
                var token = '';

                if (FStack.length > 0) {
                    tokenID = FStack[0].tokenID;
                    token = FStack[0].token;
                    FStack.shift();
                }
                else {
                    if (FIndex >= FVersionString.length) {
                        tokenID = Token.End;
                    }
                    else {
                        while (FIndex < FVersionString.length) {
                            var c = FVersionString.charAt(FIndex);

                            if ((tokenID == Token.Uninitialized || tokenID == Token.Alpha) &&
                                isLetter(c) == true) {
                                tokenID = Token.Alpha;
                                FIndex++;
                                token += c;
                            }
                            else if ((tokenID == Token.Uninitialized || tokenID == Token.Digits) &&
                                     isDigit(c) == true) {
                                if (parseInt(c) == 0 && parseInt(token) == 0) {
                                    tokenID = Token.Unknown;
                                    token += c;
                                    FIndex++;
                                    break;
                                }
                                else {
                                    tokenID = Token.Digits;
                                    token += c;
                                    FIndex++;
                                }
                            }
                            else if ((tokenID == Token.Alpha || tokenID == Token.Identifier) &&
                                     isDigit(c) == true &&
                                     isLetter(c) == false) {
                                tokenID = Token.Identifier;
                                FIndex++;
                                token += c;
                            }
                            else if (tokenID == Token.Uninitialized) {
                                switch(c) {
                                    case '-':
                                      tokenID = Token.Minus;
                                      FIndex++;
                                      token = c;
                                      break;
                                    case '+':
                                      tokenID = Token.Plus;
                                      FIndex++;
                                      token = c;
                                      break;
                                    case '*':
                                      tokenID = Token.Star;
                                      FIndex++;
                                      token = c;
                                      break;
                                    case '.':
                                      tokenID = Token.Dot;
                                      FIndex++;
                                      token = c;
                                      break;
                                    case '_':
                                      tokenID = Token.Underbar;
                                      FIndex++;
                                      token = c;
                                      break;
                                    default:
                                        tokenID = Token.Unknown;
                                        FIndex++;
                                        break;
                                }

                                break;
                            }
                            else {
                              break;
                            }
                        }
                    }
                }

                FCurrentToken = {
                    token: token,
                    tokenID: tokenID
                }

                return FCurrentToken;
            }

            return {
                start: start,
                nextToken: nextToken,
                pushToken: pushToken,
                currentToken: currentToken,
                isDigit: isDigit,
                isLetter: isLetter
            }
        }

