import pandas as pd
from pymongo import MongoClient
import certifi

from config.mongo import *
from config.features import *
from config.frameworks import *

client = MongoClient(MONGO_DB_URI, tlsCAFile=certifi.where())


def load_repos():
    return list(client[MONGO_DB_DATABASE_NAME][MONGO_DB_PROJECTS_COLLECTION_NAME].aggregate([{
        '$project': {
            '_id': False
        }
    }]))


def load_repos_for_basic_stats():
    repos = {}

    project = {
        '_id': False,
    }
    for feature in BASIC_STATS_FEATURES:
        project[feature] = True

    for feature_group in list(BASIC_STATS_PIES.values()):
        for feature in list(feature_group.values()):
            project[feature] = True

    for framework in FRAMEWORKS:
        repos[framework] = list(client.repositories.final.aggregate([{
            '$sort': {'stars': 1}
        }, {
            '$addFields': {
                'debtToProdRatio': {
                    '$divide': [
                        {'$toInt': '$sonar.measures.maintainability.workDur.sqaleIndex'},
                        {'$toInt': '$sonar.measures.maintainability.string.developmentCost'}
                    ]
                },
                'lines': {'$toInt': '$sonar.measures.size.int.ncloc'}
            }
        }, {
            '$match': {
                'sonar.measures.complexity.int.cognitiveComplexity': {'$exists': True},
                'lines': {'$gte': 1000},
                'framework': framework
            }
        }, {
            '$project': project
        }]))

    frameworks_repos_lens = []

    for framework in FRAMEWORKS:
        frameworks_repos_lens.append(len(repos[framework]))

    min_framework = min(frameworks_repos_lens)

    framework_repos = {}
    for framework in FRAMEWORKS:
        framework_repos[framework] = list(map(
            lambda repo: pd.json_normalize(repo, sep='.').to_dict(orient='records')[0],
            repos[framework][len(repos[framework]) - min_framework:]
        ))

    return framework_repos


